```php 
 SAMPLE PHP CODE 

 ================================  

 // Request parameters can be placed in request body 

 $data_string = json_encode(array( 
   'statusFilter' => 0, 
   'companyTypeFilter' => 'ONLINE_PORTAL'   
 )); 

 $ch = curl_init('https://api.getmyinvoices.com/accounts/v3/companies'); 
 curl_setopt($ch, CURLOPT_POSTFIELDS, $data_string); 
 curl_setopt($ch, CURLOPT_HEADER, 0); 
 curl_setopt($ch, CURLOPT_HTTPHEADER, array( 
   'Content-Type: application/json', 
   'Content-Length: '.strlen($data_string), 
   'X-API-KEY: XXXX-XXXX-XXXX-XXXX-XXXX-XXXX-XXXX-XXXX') 
 ); 
 curl_setopt($ch, CURLOPT_CUSTOMREQUEST, 'GET'); 
 curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1); 
 curl_setopt($ch, CURLOPT_TIMEOUT, 120); 
 curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, true); 
 curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, true); 
 $return_data = curl_exec($ch); 
 curl_close($ch); 


 // Request parameters can be placed in request url as query format instead of using request body 

 $ch = curl_init('https://api.getmyinvoices.com/accounts/v3/companies?statusFilter=0&companyTypeFilter=ONLINE_PORTAL'); 
 curl_setopt($ch, CURLOPT_HEADER, 0); 
 curl_setopt($ch, CURLOPT_HTTPHEADER, array( 
   'Content-Type: application/json', 
   'X-API-KEY: XXXX-XXXX-XXXX-XXXX-XXXX-XXXX-XXXX-XXXX') 
 ); 
 curl_setopt($ch, CURLOPT_CUSTOMREQUEST, 'GET'); 
 curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1); 
 curl_setopt($ch, CURLOPT_TIMEOUT, 120); 
 curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, true); 
 curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, true); 
 $return_data = curl_exec($ch); 
 curl_close($ch); 
 // Multiple array values are allowed in this format 
 $ch = curl_init('https://api.getmyinvoices.com/accounts/v3/documents/?tagFilter[]=img&tagFilter[]=select'); 


 // Return data will have a json data like  

 [{ 
    'companyUid': 30746, 
    'name': '1&1.de', 
    'companyType': 'ONLINE_PORTAL' 
    'note': 'Online-Portal', 
    'tags': ['Internet', 'DSL', 'VoIP'], 
    'supplierUid': 307  
 }] 
 ```
